    """Send metric to CloudWatch"""
    cloudwatch.put_metric_data(
        Namespace='FraudDetection/LogAnalysis',
        MetricData=[{
            'MetricName': metric_name,
            'Value': value,
            'Unit': 'Count',
            'Dimensions': [
                {
                    'Name': 'LogGroup',
                    'Value': log_group
                }
            ],
            'Timestamp': datetime.utcnow()
        }]
    )

